import React from 'react';
import "./index.scss";


const styles={

    transparentCard:{
        backgroundColor: "rgba(0, 0, 0, 0)",
        borderWidth: "5px",
        borderStyle: "solid",
        borderColor: "white",
        width: "170px",
        height: "170px",
        margin: "15px",
        display: "inline-block"
    }
        
}
function Card(props) {

        return (
            <div>
         

                   <button style={styles.transparentCard}src={props.image}  name ={props.title}  alt="placeholder" value = {props.value} onMouseUp={props.handleIncrement} className="card card-img-top" alt="..."></button>
                  
            
            </div>
            )
            }
        
export default Card;
