import React from "react";
import background from "../images/background.jpg";
import "./index.scss";
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
    Redirect,
    useLocation
  } from "react-router-dom";
import Portfolio from "./portfolio";
import NoMatch from "./nomatch";


const styles = {
    background: {
        backgroundImage: `url(${background})`,
        color: "white",
        height: "100vh",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
    },
    heading:{
   
        color: "white",
        textDecoration: "none"
        
    },
    
    tagline:{
  
        marginLeft: "5px",
        marginTop: "-10px"
    },
    button:{
        marginLeft: "-8px"
     
    }, 
    button2:{
        marginLeft: "5px"
    },
    h1:{
        fontWeight: "500",
        color: "white",
        textShadow: " 0 2px black"
    }
    


};

function MainPage() {
    return (
        <div style={styles.background}>
           
        
          
            <div className="container">
            <Router>
                <Link to="/" style={styles.heading}><h1 style={styles.h1}className="display-4 fadeUp">Clicky Game</h1></Link>
                <p style={styles.tagline} className="lead fadeUp">Click on </p>
               
                <Switch>
                <Route exact path="/" component={this}/>
        
                <Route exact path="/portfolio" component={Portfolio}/>

                <Route component={NoMatch} />
                </Switch>
            </Router>
            </div>
        </div>
    )
}

export default MainPage;
