import React, { Component } from 'react'
import "./index.scss";
import Card from './Card';
import Cards from "./cards.json";


import "./index.scss";



var alreadyPicked = [];
const styles = {
  
    heading:{
   
        color: "white",
        textDecoration: "none"
        
    },
    
    tagline:{
  
        marginLeft: "5px",
        marginTop: "-10px"
    },
    button:{
        marginLeft: "-8px"
     
    }, 
    button2:{
        marginLeft: "5px"
    },

    



    center: {

        borderStyle: "solid",
        borderWidth: "5px",
        borderRadius: "5px",
        opacity: "1",
        color: "white",
        backgroundColor: "rgba(0,0,0,.7)",
  

        height:"100%",
       
    },
    fontSize: {
        fontSize: "0.9rem",
        marginTop: "15px"
    }
}

function shuffleArray(array) {
    let i = array.length - 1;
    for (; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array;
  }
class GameBoard extends Component{

    state={
        count: 0,
        name: "",
        status: "not yet started"
    }

    handleIncrement = event => {
        const {value } = event.target;

        // Updating the input's state
        this.setState({
          
          count: this.state.count + 1,
          status: "winning"
        });

        console.log(value)

        for(let i=0;i<alreadyPicked.length;i++){
     
        if(value===alreadyPicked[i]){
            console.log("worked")
            this.setState({
                status: "lose",
                count: 0
            })
            alreadyPicked = []
            
        }else{
            alreadyPicked.push(value)
        }
  
        }
       
      
    }

 
    render(){

        const shuffledCards = shuffleArray(Cards)
        return (

            <div>
                   
           <h1>Clicky Game Points: {this.state.count}</h1><h2> Status: {this.state.status}</h2>
        
       
   
                <div className="fadeUp" style={styles.center}>
                <div className="row">
                {shuffledCards.map(Cards => (
                    <div className={Cards.format}>
                    <Card
                    value={Cards.title}
               
                    image={Cards.src}
                    title={Cards.title}
                    description={Cards.description}
                    projectURL={Cards.projectURL}
                    githuborheroku={Cards.githuborheroku}
                    handleIncrement={this.handleIncrement}
                    />
                    </div>
                ))}
                </div>
                </div>
              
               
            </div>
           
        )
                }
    }
export default GameBoard;
