import React, { Component } from 'react'
import "./index.scss";
import Card from './Card';
import Cards from "./cards.json";


import "./index.scss";




const styles = {
  
    heading:{
   
        color: "white",
        textDecoration: "none"
        
    },
    
    tagline:{
  
        marginLeft: "5px",
        marginTop: "-10px"
    },
    button:{
        marginLeft: "-8px"
     
    }, 
    button2:{
        marginLeft: "5px"
    },

    



    center: {

        borderStyle: "solid",
        borderWidth: "5px",
        borderRadius: "5px",
        opacity: "1",
        color: "white",
        backgroundColor: "rgba(0,0,0,.7)",
  

        height:"100%",
       
    },
    fontSize: {
        fontSize: "0.9rem",
        marginTop: "15px"
    }
}
class GameBoard extends Component{

    state={
        count: 0,
        name: "",
        status: "not yet started"
    }

    handleIncrement = event => {
        const { value } = event.target;
        console.log(this.state.name)
        if({value}===this.state.name){
            this.setState({
                status: "lose"
            })
        }
        // Updating the input's state
        this.setState({
          name: value,
          count: this.state.count + 1,
          status: "win"
        });

    }
    render(){

 
        return (

            <div>
                   
           <h1>Clicky Game Points: {this.state.count}</h1><h2> Status: {this.state.status}</h2>
        
       
   
                <div className="fadeUp" style={styles.center}>
                <div className="row">
                {Cards.map(Cards => (
                    <div className={Cards.format}>
                    <Card
                    value={Cards.title}
                    image={Cards.src}
                    title={Cards.title}
                    description={Cards.description}
                    projectURL={Cards.projectURL}
                    githuborheroku={Cards.githuborheroku}
                    handleIncrement={this.handleIncrement}
                    />
                    </div>
                ))}
                </div>
                </div>
              
               
            </div>
           
        )
                }
    }
export default GameBoard;
