import React, { Component } from 'react'
import "./index.scss";
import Card from './Card';
import Cards from "./cards.json";

import background from "../images/background.jpg";
import "./index.scss";
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
    Redirect,
    useLocation
  } from "react-router-dom";

import NoMatch from "./nomatch";


const styles = {
    background: {
        backgroundImage: `url(${background})`,
        color: "white",
        height: "100vh",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
    },
    heading:{
   
        color: "white",
        textDecoration: "none"
        
    },
    
    tagline:{
  
        marginLeft: "5px",
        marginTop: "-10px"
    },
    button:{
        marginLeft: "-8px"
     
    }, 
    button2:{
        marginLeft: "5px"
    },
    h1:{
        fontWeight: "500",
        color: "white",
        textShadow: " 0 2px black"
    },
    



    center: {

        borderStyle: "solid",
        borderWidth: "5px",
        borderRadius: "5px",
        opacity: "1",
        color: "white",
        backgroundColor: "rgba(0,0,0,.7)",
  

        height:"100%",
       
    },
    fontSize: {
        fontSize: "0.9rem",
        marginTop: "15px"
    }
}
class GameBoard extends Component{

    state={
        count: 0,
        name: "",
    }

    handleIncrement = event => {
        const { value } = event.target;

        // Updating the input's state
        this.setState({
          name: value,
          count: this.state.count + 1
        });

    }
    render(){

 
        return (

            <div>
                   <div style={styles.background}>
           
        
          
           <div className="container">
           <Router>
             <h1 style={styles.h1}className="display-4 fadeUp">Clicky Game</h1>
         
              
               <Switch>
               <Route exact path="/" component={this}/>
       
               <Route exact path="/gameboard" component={GameBoard}/>

               <Route component={NoMatch} />
               </Switch>
           </Router>
           </div>
   
                <div className="fadeUp" style={styles.center}>
                <div className="row">
                {Cards.map(Cards => (
                    <div className={Cards.format}>
                    <Card
                    name={Cards.title}
                    value={Cards.title}
                    image={Cards.src}
                    title={Cards.title}
                    description={Cards.description}
                    projectURL={Cards.projectURL}
                    githuborheroku={Cards.githuborheroku}
                    handleIncrement={this.handleIncrement}
                    />
                    </div>
                ))}
                </div>
                </div>
              
               
            </div>
            </div>
        )
                }
    }
export default GameBoard;
