import React from 'react';
import "./index.scss";


const styles={

    transparentCard:{
        backgroundColor: "rgba(0, 0, 0, 0)",
        borderWidth: "5px",
        borderStyle: "solid",
        borderColor: "white",
        width: "170px",
        margin: "15px",
        display: "inline-block"
    }
        
}
function Card(props) {

        return (
            <div>
              
                <button className="card" style={styles.transparentCard}  id={props.title}>

                   <img src={props.image}  name ={props.title}  alt="placeholder" value = {props.value} onMouseUp={props.handleIncrement} className="card-img-top" alt="..."/>
                  
                </button>
            </div>
            )
            }
        
export default Card;
