import React, { Component } from 'react'
import "./index.scss";
import Card from './Card';
import Cards from "./cards.json";


const styles = {
    center: {

        borderStyle: "solid",
        borderWidth: "5px",
        borderRadius: "5px",
        opacity: "1",
        color: "white",
        backgroundColor: "rgba(0,0,0,.7)",
  

        height:"100%",
       
    },
    fontSize: {
        fontSize: "0.9rem",
        marginTop: "15px"
    }
}
const GameBoard = () =>{
  

 
        return (

            <div>
             
                <div className="container fadeUp" style={styles.center}>
                <div className="row">
                {Cards.map(Cards => (
                    <div className={Cards.format}>
                    <Card
                    image={Cards.src}
                    title={Cards.title}
                    description={Cards.description}
                    projectURL={Cards.projectURL}
                    githuborheroku={Cards.githuborheroku}
                    />
                    </div>
                ))}
                </div>
                </div>
              
               
            </div>

        )
    }
export default GameBoard;
