import React, { Component } from 'react'
import "./index.scss";
import Card from './Card';
import Cards from "./cards.json";


const styles = {
    center: {

        borderStyle: "solid",
        borderWidth: "5px",
        borderRadius: "5px",
        opacity: "1",
        color: "white",
        backgroundColor: "rgba(0,0,0,.7)",
  

        height:"100%",
       
    },
    fontSize: {
        fontSize: "0.9rem",
        marginTop: "15px"
    }
}
export default class gameboard extends Component{

    state={
        count: 0,
        id: "",
    }

    handleIncrement = event => {
        const { name, value } = event.target;

        // Updating the input's state
        this.setState({
          [name]: value
        });
        this.setState({count: this.state.count + 1, id: this.id })
    }
    render(){

 
        return (

            <div>
             
                <div className="container fadeUp" style={styles.center}>
                <div className="row">
                {Cards.map(Cards => (
                    <div className={Cards.format}>
                    <Card
                    name={Cards.title}
                    value={Cards.title}
                    image={Cards.src}
                    title={Cards.title}
                    description={Cards.description}
                    projectURL={Cards.projectURL}
                    githuborheroku={Cards.githuborheroku}
                    handleIncrement={this.handleIncrement}
                    />
                    </div>
                ))}
                </div>
                </div>
              
               
            </div>

        )
                }
    }
